import React from "react";

const Api_Key = "798fedb7c4d7617458edf84cb603ed6b";

class Form extends React.Component {
  constructor() {
    super();
    // this.handleChange = this.handleChange.bind(this);
  }
  handleInputChange = event => {
    let adress = event.target.value;

    console.log("adress: " + adress);
  };

  getWeather = async e => {
    e.preventDefault();

    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;

    console.log("city: " + city);

    const fech = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=${Api_Key}`
    );
    const data = await fech.json();

    console.log(data);
  };

  render() {
    return (
      <div>
        <form className="form-inline">
          <label for="city">City</label>
          <input
            type="text"
            className="form-control"
            id="city"
            name="city"
            onChange={this.handleChange}
          />
          <label for="country">Country</label>
          <input
            type="text"
            className="form-control"
            id="country"
            name="country"
            onChange={this.handleChange}
          />
          <button className="btn btn-primary" onClick={this.getWeather}>
            get Weather
          </button>
        </form>
      </div>
    );
  }
}

export default Form;
